// const a = await import("./../json/ability_info.json");

export default function abilityHandler(req, res) {
	const {
		query: { id, name },
		method,
	} = req;
	const q = a[id];
	switch (method) {
		case "GET":
			// Get data from your database
			res.status(200).json(q);
			break;
		default:
			res.setHeader("Allow", ["GET"]);
			res.status(405).end(`Method ${method} Not Allowed`);
	}
}
