import { NextApiRequest, NextApiResponse } from "next";

export default async (req: NextApiRequest, res: NextApiResponse) => {
	const {
		query: { id },
		method,
	} = req;

	switch (method) {
		case "GET":
			const data = await fetch("/json/ability_info.json");
			const abilityList = data.json();

			if (abilityList[id as string]) {
				res.status(200).json(abilityList[id as string]);
			}

			throw new Error("Cannot find ability data");
			break;
		default:
			res.setHeader("Allow", ["GET"]);
			res.status(405).end(`Method ${method} Not Allowed`);
	}
};
